/**
 * Include default fonts support by browser
 *
 * @since 2.0
 * @return array list $penci_font_browser_arr
 */
if ( ! function_exists( 'penci_font_browser' ) ) {
	function penci_font_browser() {
		$penci_font_browser_arr = array();
		$penci_font_browser     = array(
			'Arial, Helvetica, sans-serif',
			'Arial Black, Gadget, sans-serif',
			'Comic Sans MS, cursive, sans-serif',
			'Impact, Charcoal, sans-serif',
			'Lucida Sans Unicode, Lucida Grande, sans-serif',
			'Tahoma, Geneva, sans-serif',
			'Trebuchet MS, Helvetica, sans-serif',
			'Verdana, Geneva, sans-serif',
			'Georgia, serif',
			'Palatino Linotype, Book Antiqua, Palatino, serif',
			'Times New Roman, Times, serif',
			'Courier New, Courier, monospace',
			'Lucida Console, Monaco, monospace',
		);
		foreach ( $penci_font_browser as $font ) {
			$penci_font_browser_arr[ $font ] = $font;
		}

		return $penci_font_browser_arr;
	}
}

/**
 * Merge 2 array fonts to one array
 *
 * @since 1.0
 * @return array fonts $penci_font_browser_arr
 */
if ( ! function_exists( 'penci_all_fonts' ) ) {
	function penci_all_fonts() {
		return array_merge( 
			penci_get_custom_fonts(),
			penci_list_google_fonts_array(),
			penci_font_browser(),
			penci_font_google_earlyaccess()
		);
	}
}
if ( ! function_exists( 'penci_google_fonts_parse_attributes' ) ) {
	function penci_google_fonts_parse_attributes( $font_family ) {
		if ( in_array( $font_family, penci_font_browser() ) ) {
			return $font_family;
		}

		$font_family     = str_replace( '"', '', $font_family );
		$font_explo      = explode( ", ", $font_family );
		$font            = isset( $font_explo[0] ) ? $font_explo[0] : '';
		$font_serif      = isset( $font_explo[2] ) ? $font_explo[2] : '';

		$font_family_end = "'" . $font . "'";

		if( $font_serif ) {
			$font_family_end .= ', ' . $font_serif;
		}

		return $font_family_end;
	}
}
if ( ! function_exists( 'penci_build_safe_css_class' ) ) {
	function penci_build_safe_css_class( $class ) {
		return preg_replace( '/\W+/', '', strtolower( str_replace( ' ', '_', strip_tags( $class ) ) ) );
	}
}

if ( ! function_exists( 'penci_get_custom_fonts' ) ) {
	function penci_get_custom_fonts() {
		$fontfamily1  = penci_get_option( 'pennews_custom_fontfamily1' );
		$fontfamily2  = penci_get_option( 'pennews_custom_fontfamily2' );
